import React,{useState} from 'react';

import MoviesList from './components/MoviesList';
import './App.css';

function App() {
  const [moviesList,setMovieList] = useState([]);
  const [isLoading,setIsLoading] = useState(false);
  const [error,setError] = useState(null)
  const fetchMOvieHandler = async() => {
    setIsLoading(true);
    setError(null);
    try {
      let response = await fetch('https://swapi.dev/api/films')
      if(!response.ok){
        throw new Error("something went wrong")
      }
      let data = await response.json()

        let movieList = data.results.map(movie =>{
          return {
            id: movie.episode_id,
            title: movie.title,
            releaseDate: movie.release_date,
            openingText: movie.opening_crawl,
        }

        })
        setMovieList(movieList)

    }
    catch(error){
        setError(error.message)


    }
    setIsLoading(false)

  }



  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMOvieHandler}>Fetch Movies</button>
      </section>
      <section>
        {isLoading && <h1>Loading...</h1>}
     { !isLoading && moviesList.length === 0  && <h1>Click on button to get movies list.</h1>}
     { !isLoading && moviesList.length > 0 &&  <MoviesList movies={moviesList} />}
     { !isLoading && error  && <h1> Filed to get movie</h1>}
      </section>
    </React.Fragment>
  );
}

export default App;
